document.addEventListener('DOMContentLoaded', function (event) {
  let name_modal = this.getElementById('name_modal');
  let form_modal = this.getElementById("form_modal");
  let name_edit = this.getElementById('company_name');
  let address_edit = this.getElementById('company_address');
  let type_method = this.getElementById('method_put');
  let method = '<input type="hidden" name="_method" value="put" />';
  let button = this.getElementById('buton_action_modal');

  this.addEventListener('click', add)
  this.addEventListener('click', edit)
  this.addEventListener('click', show)

  function add(){
    name_modal.innerHTML= 'Add Company';
    form_modal.action = '/companies'
    name_edit.value = "";
    address_edit.value = "";
    type_method.innerHTML ="";
    button.classList.remove('d-none');
  }

  function edit(id,name,address){
    name_modal.innerHTML= 'Edit Company';
    form_modal.action = '/companies/'+id;
    name_edit.value = name;
    address_edit.value = address;
    type_method.innerHTML =method;
    button.classList.remove('d-none');
  }

  function show(name,address){
    name_modal.innerHTML= 'Show Company';
    form_modal.action = ''
    name_edit.value = name;
    address_edit.value = address;
    type_method.innerHTML ='';
    button.classList.add('d-none');
  }
});
